<!DOCTYPE html><html><head>	<meta charset="utf-8">	<title>Registration Form</title>	<link rel="stylesheet" type="text/css" href="formstyle.css">	<script src="registrationForm.js"></script></head><body>		<div class="box">			<h2>Registration Form</h2>			<form method="POST" action="<?php echo htmlspecialchars($_SERVER['PHP_SELF']);?>" name="regForm">				<div class="inputBox">					<input type="text" name="username" required="" onkeyup="validateUsername(this.value)"></input>					<label>Username</label>					<div  id="user_error"></div>				</div>				<div class="inputBox">					<input type="password" name="password" required="" onkeyup="validatePassword(this.value)"></input>					<label>Password</label>					<div  id="pass_error"></div>				</div>				<div class="inputBox">					<input type="password" name="password1" required="" onkeyup="confirmPassword2()"></input>					<label>Confirm Password</label>					<div  id="pass1_error"></div>				</div>				<div class="inputBox">					<input type="text" name="name" required="" onkeyup="validateName(this.value)"></input>					<label>Full name</label>					<div  id="name_error"></div>				</div>				<div class="inputBox">					<input type="text" name="address" required="" onkeyup="validateAddress(this.value)"></input>					<label>Address</label>					<div id="addr_error"></div>				</div>				<div class="inputBox con">					<label id="lb1">Country:</label>					<select name="country" id="country" onchange="fetchState(this.value)" onclick="validateCountry(this.value)">						<option selected="" value="Default">Please select a country</option>						<!--Server response will be injected here-->						<script>							fetchCountry();						</script>						</select>					<div  id="con_error"></div>				</div>				<div class="inputBox con">					<label id="lb1">State:</label>					<select name="state" id="state" onclick="validateState(this.value)">						<option selected="" value="Default">Please select a state</option>						<!-- Server response will be injected here-->					</select>					<div  id="state_error"></div>				</div>				<div class="inputBox">					<input type="text" name="zip" value="" required="" onkeyup="validateZIP(this.value)"></input>					<label>ZIP Code</label>					<div   id="zip_error"></div>				</div>				<div class="inputBox">					<input type="text" name="email" required="" onkeyup="validateEmail(this.value)"></input>					<label id="label">Email</label>					<div  id="email_error"></div>				</div>				<div class="c1 gen">					<label>Gender: </label>					<input type="radio" name="sex" value="Male" id="male" checked/><label for="male" class="lb">Male</label>					<input type="radio" name="sex" value="Female" id="female" /><label for="female"class="lb">Female</label>					<div  id="gen_error"></div>				</div>				<div class="c1 lang">					<label>Language: </label>					<input type="checkbox"  name="lang1" value="English" id="en" checked /><label for="en" class="lb">English</label>					<input type="checkbox" name="lang2" value="Hindi" id="hd"/><label for="dg" class="lb">Hindi</label>				</div>				<div class="c2">					<label>About:</label>					<textarea id="textarea" name="comment" ></textarea>				</div>				<input type="submit" name="register" id="btn" value="Register" onclick="return validateForm()">			</form>		</div></body></html><?php	if($_SERVER["REQUEST_METHOD"] == "POST")	{		$username = dataMining($_POST['username']);		$password = dataMining($_POST['password']);		$fullname = dataMining($_POST['name']);		$gender = dataMining($_POST['sex']);		$email = dataMining($_POST['email']);		$address = dataMining($_POST['address']);		$state = dataMining($_POST['state']);		$country = dataMining($_POST['country']);		$zip = dataMining($_POST['zip']);		$lang1 = dataMining($_POST['lang1']);		$lang2 = dataMining($_POST['lang2']);		$lang = $lang1.",".$lang2;		$about = dataMining($_POST['comment']);		}	function dataMining($data) 	{	  $data = trim($data);	  $data = stripslashes($data);	  $data = htmlspecialchars($data);	  return $data;	}	$conn = mysqli_connect("localhost","root","root","formData");	if(!$conn)		die("Database connectivity interrupted: ". mysqli_connect_error());	else	{		$q = "SELECT * FROM formInfo WHERE username = '$username'";		$result = mysqli_query($conn,$q);		if(mysqli_num_rows($result)>0)			echo "Registration failed!"."<br/>"."Username already exists, try another one";		else		{			$query = "INSERT INTO formInfo(username,password,fullname,gender,email,address,state,country,zipcode,language,about) VALUES('$username','$password','$fullname','$gender','$email','$address','$state',		'$country',$zip,'$lang','$about')";		if(mysqli_query($conn,$query))			{			   echo "!! Registration successfully Done !!";			   mysqli_close($conn);			}		else			{			 if(isset($_POST['register']))				 "!! Oops, Registration failed !!";			}		}	}?>